package com.vrv.cems.service.cache.bean;

public class DevicePolicyBean {
	/**
	 * 设备唯一ID
	 */
	private String devOnlyId;
	/**
	 * 策略总版本(具体计算格式详见《VRV-VDP-064-缓存数据结构设计-CEMS.doc》文档中“6.设备执行策略信息”)
	 */
	private String policyVersion;
	/**
	 * 策略ID，多个以","分开
	 */
	private String policyIds;
	public DevicePolicyBean() {
		super();
	}
	public DevicePolicyBean(String devOnlyId, String policyVersion,
			String policyIds) {
		super();
		this.devOnlyId = devOnlyId;
		this.policyVersion = policyVersion;
		this.policyIds = policyIds;
	}
	public String getDevOnlyId() {
		return devOnlyId;
	}
	public void setDevOnlyId(String devOnlyId) {
		this.devOnlyId = devOnlyId;
	}
	public String getPolicyVersion() {
		return policyVersion;
	}
	public void setPolicyVersion(String policyVersion) {
		this.policyVersion = policyVersion;
	}
	public String getPolicyIds() {
		return policyIds;
	}
	public void setPolicyIds(String policyIds) {
		this.policyIds = policyIds;
	}
	
	
	
}
